import React, { useState } from 'react';
import Map from '../components/Map';
import ReportIncidentForm from '../components/ReportIncidentForm';
import axios from 'axios'; // Ajusta o usa tu API client

const Home = () => {
  const [showReportForm, setShowReportForm] = useState(false);
  const [selectedCoordinates, setSelectedCoordinates] = useState(null);

  const handleCoordinatesSelected = (coords) => {
    setSelectedCoordinates(coords);
  };

  const handleReportSubmit = async (incidentData) => {
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/incidents`, incidentData);
      alert('Incidente reportado con éxito');
      setShowReportForm(false);
    } catch (error) {
      console.error(error);
      alert('Error al reportar incidente');
    }
  };

  return (
    <div className="container mt-4">
      <h1>Consulta de Rutas y Transitabilidad</h1>
      <button className="btn btn-success mb-3" onClick={() => setShowReportForm(true)}>Reportar Incidente</button>
      <Map onCoordinatesSelected={handleCoordinatesSelected} />
      <ReportIncidentForm 
        show={showReportForm} 
        onHide={() => setShowReportForm(false)} 
        onSubmit={handleReportSubmit}
        selectedCoordinates={selectedCoordinates}
      />
    </div>
  );
};

export default Home;
