import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
// Aquí necesitarás un endpoint para traer las solicitudes de incidencia
// Por ahora es solo un placeholder

function IncidentRequests() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    loadRequests();
  }, []);

  async function loadRequests() {
    // Llamar a la API para traer las solicitudes
    // Por ahora, simulemos:
    setRequests([
      { id: 1, detail: 'Incidente cerca del puente', photoUrl: '...' },
      { id: 2, detail: 'Bloqueo en la carretera principal', photoUrl: '...' }
    ]);
  }

  function handleApprove(requestId) {
    // Lógica para crear incidente real a partir de la solicitud
    alert('Incidente creado a partir de la solicitud #' + requestId);
  }

  return (
    <div>
      <h2>Solicitudes de Incidencia</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Detalle</th>
            <th>Foto</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {requests.map(r=>(
            <tr key={r.id}>
              <td>{r.detail}</td>
              <td><img src={r.photoUrl} alt="Foto incidencia" width="100"/></td>
              <td><Button onClick={()=>handleApprove(r.id)}>Aprobar</Button></td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default IncidentRequests;
