import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

export async function fetchRoads() {
  const token = localStorage.getItem('token');
  const res = await axios.get(`${API_URL}/roads`, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return res.data;
}

export async function fetchMunicipalities() {
  const token = localStorage.getItem('token');
  const res = await axios.get(`${API_URL}/municipalities`, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return res.data;
}

export async function fetchIncidents() {
  const token = localStorage.getItem('token');
  const res = await axios.get(`${API_URL}/incidents`, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return res.data;
}

export async function fetchUsers() {
  const token = localStorage.getItem('token');
  const res = await axios.get(`${API_URL}/users`, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return res.data;
}

export async function createUser(email, password, role) {
  const token = localStorage.getItem('token');
  const res = await axios.post(`${API_URL}/users`, { email, password, role }, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return res.data;
}

export async function updateUserPassword(userId, newPassword) {
  const token = localStorage.getItem('token');
  const res = await axios.put(`${API_URL}/users/${userId}/password`, { newPassword }, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return res.data;
}
