import React from 'react';
import { GoogleMap, LoadScript, Polyline, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '500px'
};

const center = { lat: -16.2902, lng: -63.5887 }; // Centro aprox de Bolivia

const Map = ({ roads, municipalities, incidents, highlightRoad }) => {
  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={5}>
        {roads.map((road) => (
          <Polyline
            key={road.id}
            path={road.points}
            options={{ strokeColor: road.blocked ? 'red' : 'blue' }}
          />
        ))}

        {municipalities.map((m) => (
          <Marker key={m.id} position={m.coordinates} />
        ))}

        {incidents.map((inc) => (
          <Marker
            key={inc.id}
            position={inc.coordinates}
            icon="http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"
          />
        ))}

        {highlightRoad && (
          <Polyline
            path={highlightRoad.points}
            options={{ strokeColor: highlightRoad.blocked ? 'red' : 'green', strokeWeight: 6 }}
          />
        )}

        {highlightRoad && (
          <>
            {/* Marcamos origen y destino con marcadores personalizados, por ejemplo */}
            <Marker position={highlightRoad.points[0]} icon="http://maps.google.com/mapfiles/ms/icons/green-dot.png" />
            <Marker position={highlightRoad.points[highlightRoad.points.length-1]} icon="http://maps.google.com/mapfiles/ms/icons/red-dot.png" />
          </>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
