import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import { fetchRoads } from '../../services/api';

function RoadsManagement() {
  const [roads, setRoads] = useState([]);

  useEffect(() => {
    loadRoads();
  }, []);

  async function loadRoads() {
    const data = await fetchRoads();
    setRoads(data);
  }

  return (
    <div>
      <h2>Gestión de Carreteras</h2>
      {/* Aquí iría el formulario para crear/editar carreteras y dibujarlas en el mapa */}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Origen</th>
            <th>Destino</th>
            <th>Estado</th>
          </tr>
        </thead>
        <tbody>
          {roads.map(r=>(
            <tr key={r.id}>
              <td>{r.name}</td>
              <td>{r.originName}</td>
              <td>{r.destinationName}</td>
              <td>{r.blocked ? 'Bloqueada' : 'Libre'}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default RoadsManagement;
