import React, { useEffect, useState } from 'react';
import incidentsAPI from '../../api/incidentsAPI'; // Debes tener el API para incidentes
import useAuth from '../../hooks/useAuth';

const ManageIncidents = () => {
  const { auth } = useAuth();
  const [incidents, setIncidents] = useState([]);

  useEffect(() => {
    const loadIncidents = async () => {
      const data = await incidentsAPI.getAll(auth.token);
      setIncidents(data);
    };
    loadIncidents();
  }, [auth.token]);

  const handleDelete = async (id) => {
    await incidentsAPI.delete(id, auth.token);
    setIncidents(incidents.filter(i => i.id !== id));
  };

  return (
    <div className="container mt-5">
      <h2>Gestionar Incidentes</h2>
      <button className="btn btn-primary mb-3">Crear Incidente</button>
      {incidents.length === 0 ? (
        <p>No hay incidentes registrados.</p>
      ) : (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>ID</th>
              <th>Tipo</th>
              <th>Raz√≥n</th>
              <th>Coordenadas</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {incidents.map(i => (
              <tr key={i.id}>
                <td>{i.id}</td>
                <td>{i.type}</td>
                <td>{i.reason}</td>
                <td>{`${i.coordinates.lat}, ${i.coordinates.lng}`}</td>
                <td>
                  <button className="btn btn-secondary">Editar</button>
                  <button className="btn btn-danger ms-2" onClick={() => handleDelete(i.id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ManageIncidents;
