import React, { useState } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';

const ReportIncidentForm = ({ show, onHide, onSubmit }) => {
  const [photo, setPhoto] = useState(null);
  const [detail, setDetail] = useState('');

  const handleSubmit = () => {
    onSubmit({ detail, photo });
  };

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Reportar Incidente</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form.Group className="mb-3">
          <Form.Label>Detalle</Form.Label>
          <Form.Control type="text" value={detail} onChange={(e) => setDetail(e.target.value)} />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Foto</Form.Label>
          <Form.Control type="file" onChange={(e) => setPhoto(e.target.files[0])} />
        </Form.Group>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cerrar</Button>
        <Button variant="primary" onClick={handleSubmit}>Enviar</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ReportIncidentForm;
