import React from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import { useAuth } from '../hooks/useAuth';
import { LinkContainer } from 'react-router-bootstrap'; // Asegúrate de usar v2.x

function Header() {
  const { user, logout } = useAuth();

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      {/* Ahora LinkContainer está dentro del Router (gracias a index.js) */}
      <LinkContainer to="/">
        <Navbar.Brand>Sistema Transporte</Navbar.Brand>
      </LinkContainer>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          {!user && (
            <LinkContainer to="/login">
              <Nav.Link>Iniciar Sesión</Nav.Link>
            </LinkContainer>
          )}
          {user && user.role === 'admin' && (
            <LinkContainer to="/admin">
              <Nav.Link>Admin Panel</Nav.Link>
            </LinkContainer>
          )}
          {user && user.role === 'verifier' && (
            <LinkContainer to="/verifier">
              <Nav.Link>Verificador</Nav.Link>
            </LinkContainer>
          )}
          {user && (
            <Nav.Link onClick={logout}>Cerrar Sesión</Nav.Link>
          )}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default Header;
