import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { fetchMunicipalities } from '../../services/api';

function MunicipalitiesManagement() {
  const [municipalities, setMunicipalities] = useState([]);

  useEffect(() => {
    loadMunicipalities();
  }, []);

  async function loadMunicipalities() {
    const data = await fetchMunicipalities();
    setMunicipalities(data);
  }

  return (
    <div>
      <h2>Gestión de Municipios</h2>
      {/* Aquí iría formulario para crear/editar municipios */}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Coordenadas</th>
          </tr>
        </thead>
        <tbody>
          {municipalities.map(m=>(
            <tr key={m.id}>
              <td>{m.name}</td>
              <td>{JSON.stringify(m.coordinates)}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default MunicipalitiesManagement;
