import React, { useState } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';

const ReportIncidentForm = ({ show, onHide, onSubmit }) => {
  const [type, setType] = useState('');
  const [reason, setReason] = useState('');
  const [lat, setLat] = useState('');
  const [lng, setLng] = useState('');

  const handleSubmit = () => {
    // Creamos un objeto con los datos requeridos.
    const incidentData = {
      type,
      reason,
      coordinates: {
        lat: parseFloat(lat),
        lng: parseFloat(lng)
      }
    };
    // Llamamos a onSubmit pasando este objeto.
    onSubmit(incidentData);
  };

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Reportar Incidente</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form.Group className="mb-3">
          <Form.Label>Tipo</Form.Label>
          <Form.Control 
            type="text" 
            value={type} 
            onChange={(e) => setType(e.target.value)} 
            placeholder="Ej: No transitable por conflictos sociales"
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Razón</Form.Label>
          <Form.Control 
            type="text" 
            value={reason} 
            onChange={(e) => setReason(e.target.value)} 
            placeholder="Ej: Bloqueo en la vía"
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Latitud</Form.Label>
          <Form.Control 
            type="number" 
            step="any" 
            value={lat} 
            onChange={(e) => setLat(e.target.value)} 
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Longitud</Form.Label>
          <Form.Control 
            type="number" 
            step="any" 
            value={lng} 
            onChange={(e) => setLng(e.target.value)} 
          />
        </Form.Group>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cerrar</Button>
        <Button variant="primary" onClick={handleSubmit}>Enviar</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ReportIncidentForm;
