// src/components/MapView/MapView.js
import React, { useCallback, useRef, useEffect } from 'react';
import { GoogleMap, LoadScript, Polyline, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '600px'
};

const center = {
  lat: -16.290154,
  lng: -63.588653
};

function MapView({ roads, municipalities, incidents, selectedRoadId }) {
  const mapRef = useRef();
  
  const onLoad = useCallback(map => {
    mapRef.current = map;
  }, []);

  useEffect(() => {
    if (selectedRoadId && roads && mapRef.current) {
      const road = roads.find(r => r.id === selectedRoadId);
      if (road && road.points.length > 0) {
        // Ajustar el mapa a la carretera seleccionada
        const bounds = new window.google.maps.LatLngBounds();
        road.points.forEach(p => bounds.extend(p));
        mapRef.current.fitBounds(bounds);
      }
    }
  }, [selectedRoadId, roads]);

  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={6}
        onLoad={onLoad}
      >
        {roads && roads.map(road => {
          const isSelected = road.id === selectedRoadId;
          return (
            <Polyline 
              key={road.id}
              path={road.points}
              options={{
                strokeColor: road.blocked ? (isSelected ? '#FF0000' : '#AA0000') : (isSelected ? '#00FF00' : '#008000'),
                strokeOpacity: isSelected ? 1.0 : 0.8,
                strokeWeight: isSelected ? 6 : 4
              }}
            />
          );
        })}

        {municipalities && municipalities.map(m => (
          <Marker
            key={m.id}
            position={m.coordinates}
            title={m.name}
          />
        ))}

        {incidents && incidents.map(i => (
          <Marker
            key={i.id}
            position={i.coordinates}
            icon={{
              url: 'http://maps.google.com/mapfiles/ms/icons/yellow-dot.png'
            }}
            title={i.type}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
}

export default MapView;
