// src/api/incidentsAPI.js
import axios from './axiosInstance';

const incidentsAPI = {
  getAll: async (token) => {
    const res = await axios.get('/incidents', {
      headers: { Authorization: `Bearer ${token}` }
    });
    return res.data;
  },
  create: async (incidentData, token) => {
    // incidentData es un objeto con { type, reason, coordinates: {lat, lng} }
    const res = await axios.post('/incidents', incidentData, {
      headers: { Authorization: `Bearer ${token}` }
    });
    return res.data;
  },
  delete: async (id, token) => {
    await axios.delete(`/incidents/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
  }
};

export default incidentsAPI;
