import React, { useEffect, useState } from 'react';
import { Table, Button, Form } from 'react-bootstrap';
import { fetchUsers, createUser, updateUserPassword } from '../../services/api';

function UsersManagement() {
  const [users, setUsers] = useState([]);
  const [newEmail, setNewEmail] = useState('');
  const [newRole, setNewRole] = useState('verifier');
  const [newPassword, setNewPassword] = useState('');

  useEffect(() => {
    loadUsers();
  }, []);

  async function loadUsers() {
    const data = await fetchUsers();
    setUsers(data);
  }

  async function handleCreateUser() {
    await createUser(newEmail, newPassword, newRole);
    loadUsers();
  }

  async function handleResetPassword(id) {
    const newPass = prompt('Ingrese nueva contraseña:');
    if (!newPass) return;
    await updateUserPassword(id, newPass);
    alert('Contraseña actualizada');
  }

  return (
    <div>
      <h2>Gestión de Usuarios</h2>
      <Form className="mb-3">
        <Form.Group controlId="newUserEmail">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" value={newEmail} onChange={e=>setNewEmail(e.target.value)}/>
        </Form.Group>
        <Form.Group controlId="newUserRole">
          <Form.Label>Rol</Form.Label>
          <Form.Select value={newRole} onChange={e=>setNewRole(e.target.value)}>
            <option value="admin">Admin</option>
            <option value="verifier">Verifier</option>
          </Form.Select>
        </Form.Group>
        <Form.Group controlId="newUserPass">
          <Form.Label>Contraseña</Form.Label>
          <Form.Control type="password" value={newPassword} onChange={e=>setNewPassword(e.target.value)}/>
        </Form.Group>
        <Button className="mt-2" onClick={handleCreateUser}>Crear Usuario</Button>
      </Form>
      
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Email</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u=>(
            <tr key={u.id}>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>
                <Button variant="warning" onClick={()=>handleResetPassword(u.id)}>Cambiar contraseña</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default UsersManagement;
