// src/components/AdminPanel/IncidentRequests.js
import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import { fetchIncidentRequests, approveIncidentRequest } from '../../services/api';

function IncidentRequests() {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadRequests();
  }, []);

  async function loadRequests() {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchIncidentRequests();
      setRequests(data);
    } catch (err) {
      console.error(err);
      setError('Error al cargar solicitudes');
    } finally {
      setLoading(false);
    }
  }

  async function handleApprove(requestId) {
    try {
      await approveIncidentRequest(requestId);
      alert('Incidente creado exitosamente');
      loadRequests();
    } catch (err) {
      console.error(err);
      alert('Error al aprobar solicitud');
    }
  }

  if (loading) return <p>Cargando solicitudes...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Solicitudes de Incidencia</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Detalle</th>
            <th>Foto</th>
            <th>Coordenadas</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {requests.map(r=>(
            <tr key={r.id}>
              <td>{r.detail}</td>
              <td>{r.photoUrl ? <img src={r.photoUrl} alt="Incidente" width="100"/> : 'Sin foto'}</td>
              <td>{JSON.stringify(r.coordinates)}</td>
              <td><Button onClick={()=>handleApprove(r.id)}>Aprobar</Button></td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default IncidentRequests;
