// src/components/RoadsList.js
import React, { useState } from 'react';
import { Table, Button, Modal } from 'react-bootstrap';

function RoadsList({ roads, onViewRoad }) {
  const [showMotivo, setShowMotivo] = useState(false);
  const [selectedRoad, setSelectedRoad] = useState(null);

  const handleShowMotivo = (road) => {
    setSelectedRoad(road);
    setShowMotivo(true);
  };

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Origen</th>
            <th>Destino</th>
            <th>Carretera</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {roads.map(r => (
            <tr key={r.id}>
              <td>{r.originName}</td>
              <td>{r.destinationName}</td>
              <td>{r.name}</td>
              <td>{r.blocked ? `Bloqueada: ${r.reason}` : 'Libre'}</td>
              <td>
                <Button variant="primary" onClick={() => onViewRoad(r.id)}>Ver carretera</Button>
                {r.blocked && r.reasonImage && (
                  <Button variant="warning" onClick={() => handleShowMotivo(r)}>Ver motivo</Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showMotivo} onHide={()=>setShowMotivo(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Motivo de bloqueo</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedRoad && (
            <div>
              <p>{selectedRoad.reason}</p>
              {selectedRoad.reasonImage && <img src={selectedRoad.reasonImage} alt="Motivo" style={{maxWidth: '100%'}} />}
            </div>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
}

export default RoadsList;
