import React, { useMemo } from 'react';
import { GoogleMap, LoadScript, Polyline, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '500px'
};

const center = { lat: -16.2902, lng: -63.5887 }; // Centro de Bolivia (aprox)

const Map = ({ roads, municipalities, onRoadClick, highlightRoad, incidents }) => {
  const path = highlightRoad ? highlightRoad.points : [];

  const lineOptions = useMemo(() => ({
    strokeColor: highlightRoad?.blocked ? '#FF0000' : '#00FF00'
  }), [highlightRoad]);

  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={5}>
        {roads.map((road) => (
          <Polyline
            key={road.id}
            path={road.points}
            options={{ strokeColor: road.blocked ? 'red' : 'blue' }}
            onClick={() => onRoadClick(road)}
          />
        ))}

        {municipalities.map((m) => (
          <Marker key={m.id} position={m.coordinates} />
        ))}

        {highlightRoad && (
          <Polyline
            path={path}
            options={lineOptions}
          />
        )}

        {incidents.map((inc) => (
          <Marker key={inc.id} position={inc.coordinates} icon="http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"/>
        ))}

      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
