// src/components/RoadsList.js
import React from 'react';
import { Table, Button } from 'react-bootstrap';

function RoadsList({ roads, onViewRoad }) {
  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>Origen</th>
          <th>Destino</th>
          <th>Carretera</th>
          <th>Estado</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {roads.map(r => (
          <tr key={r.id}>
            <td>{r.originName}</td>
            <td>{r.destinationName}</td>
            <td>{r.name}</td>
            <td>{r.blocked ? `Bloqueada: ${r.reason}` : 'Libre'}</td>
            <td>
              <Button variant="primary" onClick={() => onViewRoad(r.id)}>Ver carretera</Button>
              {r.blocked && (
                <Button variant="warning" onClick={() => alert("Mostrar motivo con foto")}>Ver motivo</Button>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default RoadsList;
