import React, { useState } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import incidentsAPI from '../../api/incidentsAPI';

const ReportIncidentForm = ({ show, onHide, selectedCoordinates }) => {
  const [type, setType] = useState('');
  const [reason, setReason] = useState('');

  const handleSubmit = async () => {
    if (!selectedCoordinates) {
      alert('Seleccione un punto en el mapa antes de reportar el incidente.');
      return;
    }
    const incidentData = {
      type,
      reason,
      coordinates: selectedCoordinates
    };

    try {
      const newIncident = await incidentsAPI.create(incidentData);
      console.log('Incidente creado:', newIncident);
      alert('Incidente reportado con éxito');
      onHide(); // cerrar el modal
    } catch (error) {
      console.error('Error al reportar incidente:', error);
      alert('Error al reportar incidente');
    }
  };

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Reportar Incidente</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form.Group className="mb-3">
          <Form.Label>Tipo</Form.Label>
          <Form.Control 
            type="text" 
            value={type} 
            onChange={(e) => setType(e.target.value)} 
            placeholder="Ej: No transitable por conflictos sociales"
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Razón</Form.Label>
          <Form.Control 
            type="text" 
            value={reason} 
            onChange={(e) => setReason(e.target.value)} 
            placeholder="Ej: Bloqueo en la vía"
          />
        </Form.Group>
        <p>Coordenadas seleccionadas: 
          {selectedCoordinates ? `Lat: ${selectedCoordinates.lat}, Lng: ${selectedCoordinates.lng}` : 'Ninguna'}
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cerrar</Button>
        <Button variant="primary" onClick={handleSubmit}>Enviar</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ReportIncidentForm;
